import { Form, Button, Label, Group } from "react-bootstrap";
import emailjs from "emailjs-com";
import React from "react";

export default function showForm() {
  document.querySelector("formElement").style.display = "block";
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm("gmail", "template_x2csvjx", e.target, "s965jElkUw1f4Sb_d")
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  }
  return (
    <Form>
      <Form.Group ref={Form} onSubmit={sendEmail}>
        <Form.Label>Name</Form.Label>
        <input type="text" name="user_name" />
        <Form.Label>Email</Form.Label>
        <input type="email" name="user_email" />
        <Form.Label>Message</Form.Label>
        <textarea name="message" />
        <input type="submit" value="Send" />
      </Form.Group>
      <Button type="submit"></Button>
    </Form>
  );
}
